y=12*8
s=256

function TIC()

	if btnp(0) then y=y-8 s=256 end
	if btnp(1) then y=y+8 s=256 end
	if btnp(2) then x=x-8 s=320 end
	if btnp(3) then x=x+8 s=288 end
	
--	if btnp(4) then s=352 end
	
	cls(0)
	map(0,0)
	spr(s,x,y,1,1,0,0,2,2)
	t=t+1
end

